        setBonePosition(e);
    });

    app.stage.on("globalpointermove", (e) => {
        if (isDragging) setBonePosition(e);
    });

    app.stage.on("pointerup", (e) => (isDragging = false));

    const setBonePosition = (e) => {
        // Transform the mouse/touch coordinates to Spineboy's coordinate
