                dragObject.y += y - lastY;
                lastX = x;
                lastY = y;
            }
          });

        control.on('pointerdown', ({ x, y }) => {
            spineboy.state.setAnimation(0, "shoot", false, 2.0);
          dragObject = control;
          lastX = x;
          lastY = y;
          console.log(x)
        })
      }
      spineboy.state.setAnimation(0, "walk", true);
      const point = { x: 0, y: 0 };
