import * as PIXI from 'pixi.js';
import * as spine from '@pixi-spine/runtime-4.1'; // Assuming you're using pixi-spine

export default {
  name: 'PixiSpineComponent',
  mounted() {
    this.initPixi();
  },
  methods: {
    async initPixi() {
      var app = new PIXI.Application();
      await app.init({
        width: window.innerWidth,
        height: window.innerHeight,
        resolution: window.devicePixelRatio || 1,
        autoDensity: true,
        resizeTo: window,
        backgroundColor: 0x2c3e50,
        hello: true
      });
      document.body.appendChild(app.view);

      // Pre-load the skeleton data and atlas. You can also load .json skeleton data.
      PIXI.Assets.add({
        alias: "spineboyData",
        src: "./assets/spineboy-pro.skel"
      });
      PIXI.Assets.add({
        alias: "spineboyAtlas",
        src: "./assets/spineboy-pma.atlas"
      });
      await PIXI.Assets.load(["spineboyData", "spineboyAtlas"]);

      // Create the spine display object
      const spineboy = spine.Spine.from({
        skeleton: "spineboyData",
        atlas: "spineboyAtlas",
        scale: 0.5
      });

      // Set the default mix time to use when transitioning
      // from one animation to the next.
      spineboy.state.data.defaultMix = 0.2;

      // Center the spine object on screen.
      spineboy.x = window.innerWidth / 2;
      spineboy.y = window.innerHeight / 2 + spineboy.getBounds().height / 2;

      // Set animation "run" on track 0, looped.
      spineboy.state.setAnimation(0, "run", true);

      // Add the display object to the stage.
      app.stage.addChild(spineboy);
    }
  }
};
